import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import Payments from './Payments';

class Navbar extends Component {
  componentDidMount() {
    const $navbarBurgers = Array.prototype.slice.call(
      document.querySelectorAll('.navbar-burger'),
      0
    );

    console.log($navbarBurgers.length);
    if ($navbarBurgers.length > 0) {
      $navbarBurgers.forEach(el => {
        console.log(el);
        el.addEventListener('click', () => {
          const target = el.dataset.target;
          console.log(target);
          const $target = document.getElementById(target);
          el.classList.toggle('is-active');
          $target.classList.toggle('is-active');
        });
      });
    }
  }

  renderContent() {
    switch (this.props.auth) {
      case null:
        return;
      case false:
        return (
          <a href="/auth/google" className="button is-primary">
            <strong>Login with Google</strong>
          </a>
        );

      default:
        return [
          <Payments key="1" />,
          <div key="3" className="button" style={{ marginLeft: '10px' }}>
            Credits: {this.props.auth.credits}
          </div>,
          <a key="2" href="/api/logout" className="button">
            Logout
          </a>
        ];
    }
  }

  render() {
    return (
      <nav className="navbar">
        <div className="navbar-brand">
          <Link to={this.props.auth ? '/surveys' : '/'} className="navbar-item">
            Emaily
          </Link>

          <Link
            to="#"
            role="button"
            className="navbar-burger burger"
            aria-label="menu"
            aria-expanded="false"
            data-target="navbarBasicExample"
          >
            <span aria-hidden="true" />
            <span aria-hidden="true" />
            <span aria-hidden="true" />
          </Link>
        </div>
        <div className="navbar-end">
          <div className="navbar-item">
            <div className="buttons">{this.renderContent()}</div>
          </div>
        </div>
      </nav>
    );
  }
}

function mapStateToProps({ auth }) {
  return { auth };
}

export default connect(mapStateToProps)(Navbar);
